"use client";

import { motion, AnimatePresence } from "framer-motion";
import React, { useState } from "react";
import { cn } from "@/lib/utils";
import {
  FaGithub,
  FaTwitter,
  FaFacebook,
  FaInstagram,
  FaLinkedin,
  FaEnvelope,
  FaDiscord,
} from "react-icons/fa";


export interface SocialItem {
  letter: string;
  icon: React.ReactNode;
  label: string;
  href?: string;
  onClick?: () => void;
}

interface SocialFlipButtonProps {
  items?: SocialItem[];
  className?: string;
}

const defaultItems: SocialItem[] = [
  { letter: "C", icon: <FaGithub />, label: "Github", href: "#" },
  { letter: "O", icon: <FaTwitter />, label: "Twitter", href: "#" },
  { letter: "N", icon: <FaLinkedin />, label: "LinkedIn", href: "#" },
  { letter: "T", icon: <FaInstagram />, label: "Instagram", href: "#" },
  { letter: "A", icon: <FaFacebook />, label: "Facebook", href: "#" },
  { letter: "C", icon: <FaEnvelope />, label: "Email", href: "#" },
  { letter: "T", icon: <FaDiscord />, label: "Discord", href: "#" },
];

export default function SocialFlipButton({
  items = defaultItems,
  className,
}: SocialFlipButtonProps) {
  const [isHovered, setIsHovered] = useState(false);
  const [tooltipIndex, setTooltipIndex] = useState<number | null>(null);

  return (
    <div className={cn("flex items-center justify-center gap-4 p-4", className)}>
      <div
        className="group relative flex items-center justify-center gap-2 rounded-2xl border border-neutral-200 bg-white p-4 shadow-sm dark:border-neutral-800 dark:bg-black"
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => {
          setIsHovered(false);
          setTooltipIndex(null);
        }}
      >
        {/* Border Lines Container - Clipped */}
        <div className="absolute inset-0 overflow-hidden rounded-2xl pointer-events-none">
          {/* Animated Top Border Line */}
          <motion.div
            className="absolute top-0 left-0 h-[1px] w-full bg-gradient-to-r from-transparent via-black/50 to-transparent dark:via-white/50"
            animate={{ x: ["-100%", "100%"] }}
            transition={{
              duration: 2.5,
              repeat: Infinity,
              ease: "linear",
            }}
          />

          {/* Animated Bottom Border Line */}
          <motion.div
            className="absolute bottom-0 left-0 h-[1px] w-full bg-gradient-to-r from-transparent via-black/50 to-transparent dark:via-white/50"
            animate={{ x: ["100%", "-100%"] }}
            transition={{
              duration: 2.5,
              repeat: Infinity,
              ease: "linear",
            }}
          />
        </div>

        {items.map((item, index) => {
          const Wrapper = item.href ? "a" : "div";
          const wrapperProps = item.href
            ? { href: item.href, target: "_blank", rel: "noopener noreferrer" }
            : { onClick: item.onClick };

          return (
            <Wrapper
              key={index}
              {...wrapperProps}
              className="relative h-10 w-10 cursor-pointer"
              style={{ perspective: "1000px" }}
              onMouseEnter={() => setTooltipIndex(index)}
              onMouseLeave={() => setTooltipIndex(null)}
            >
              <AnimatePresence>
                {isHovered && tooltipIndex === index && (
                  <motion.div
                    initial={{ opacity: 0, y: 10, scale: 0.8 }}
                    animate={{ opacity: 1, y: -50, scale: 1 }}
                    exit={{ opacity: 0, y: 10, scale: 0.8 }}
                    transition={{ duration: 0.2, ease: "easeOut" }}
                    className="absolute left-1/2 z-50 -translate-x-1/2 whitespace-nowrap rounded-lg bg-neutral-900 px-3 py-1.5 text-xs font-semibold text-white shadow-xl dark:bg-white dark:text-neutral-900"
                  >
                    {item.label}
                    {/* Arrow */}
                    <div className="absolute -bottom-1 left-1/2 -translate-x-1/2 h-2 w-2 rotate-45 bg-neutral-900 dark:bg-white" />
                  </motion.div>
                )}
              </AnimatePresence>

              <motion.div
                className="relative h-full w-full"
                initial={false}
                animate={{ rotateY: isHovered ? 180 : 0 }}
                transition={{
                  duration: 0.8,
                  type: "spring",
                  stiffness: 120,
                  damping: 15,
                  delay: index * 0.08,
                }}
                style={{ transformStyle: "preserve-3d" }}
              >
                {/* Front - Letter */}
                <div
                  className="absolute inset-0 flex items-center justify-center rounded-lg bg-neutral-100 text-lg font-bold text-neutral-800 shadow-sm dark:bg-neutral-900 dark:text-neutral-200"
                  style={{ backfaceVisibility: "hidden" }}
                >
                  {item.letter}
                </div>

                {/* Back - Icon */}
                <div
                  className="absolute inset-0 flex items-center justify-center rounded-lg bg-black text-lg text-white dark:bg-white dark:text-black"
                  style={{
                    backfaceVisibility: "hidden",
                    transform: "rotateY(180deg)",
                  }}
                >
                  {item.icon}
                </div>
              </motion.div>
            </Wrapper>
          );
        })}
      </div>
    </div>
  );
}
